import React from 'react';
import './index.css';

import { render } from "react-dom";
import {
  FirebaseDatabaseProvider,
  FirebaseDatabaseNode
       } from "@react-firebase/database";
import * as firebase from "firebase/app";
import "@firebase/database";
import { config } from "./config";

const styles = {
  fontFamily: "sans-serif"
};

const s = (a: any) => JSON.stringify(a, null, 2);

class App extends React.Component {
    state = {
      limit: 20
    };
    hitProper = {
      part:  0,
      power: 0,
      long:  0,
      span:  0,
      counter: 0
    };

    power = {
      Part1: 0,
      Part2: 0,
      Part3: 0,
      Part4: 0,
   }

    destructuring (string) {
      console.log("String"+string);
      if(string!==undefined){
       // console.log('nomer'+string.indexOf(':'));

      if(string.indexOf(':')===0){
        this.hitProper.part=string.slice(1,2);
        this.hitProper.long=string.slice(3,string.indexOf(":",3));
        this.hitProper.power=string.slice(string.indexOf(":",3)+1,string.indexOf(":",10));
        this.hitProper.span=string.slice(string.indexOf(":",9)+1);
      }}
   //   console.log(typeof(this.hitProper.part));
      switch(this.hitProper.part){
        case "1":
            for(let key in this.power){this.power[key]=0;}
            this.power.Part1=this.hitProper.power;
            break;
        case "2":
        for(let key in this.power){this.power[key]=0;}
            this.power.Part2=this.hitProper.power;
            break;
        case "3":
        for(let key in this.power){this.power[key]=0;}
            this.power.Part3=this.hitProper.power;
            break;
        case "4":
        for(let key in this.power){this.power[key]=0;}
            this.power.Part4=this.hitProper.power;
            break;
        default: break;

      }
    }

    render() {
      return (
        <div style={styles}>
          <FirebaseDatabaseProvider firebase={firebase} {...config}>
            <FirebaseDatabaseNode  path="hits/" //  limitToFirst={this.state.limit}
                                   orderByKey  // orderByValue={"created_on"}
                                           >
              {d => {
                this.hitProper.counter++;
                const object = d.value;
                let hitData = [];
                let pushHit = "";
               // let value=33;
                for(let key in object){
                  hitData.push(object[key].original); 
                          }
                //Object.values(object);
                console.log('check'+hitData[hitData.length-1]);
                this.destructuring(hitData[hitData.length-1]);
                if(this.hitProper.span<150){
                     pushHit="HIT";
                } else{
                  pushHit="PUSH !!!";
                }
// style={{ height: 100, overflow: "auto"}}>  class="fit-picture" 
                return (
                  <div className="bigText" ><p>HIT SYSTEM</p>
                  <img 
                       src="../body.png" alt=""
                           />
                  <React.Fragment>
                {/*<pre>Path {d.path} </pre>*/}
                    <ul className="list-group ul">
                      <li className="list">
                          <ul className="list-group list-group-horizontal-lg"> 
                            <li className="list-group-item">Value</li>
                            <li className="list-group-item">{hitData[hitData.length-1]}</li>
                      </ul></li>
                      <li className="list"><ul className="list-group list-group-horizontal-lg">
                            <li className="list-group-item">  Part: {this.hitProper.part}   </li>
                            <li className="list-group-item">{this.hitProper.part}  </li>
                      </ul></li>      
                      <li className="list-group-item">  Distance: 
                                  <span class="badge badge-primary badge-pill">{this.hitProper.long}</span> </li> 
                      <li className="list-group-item">  Power: {this.hitProper.power}</li>
                      <li className="list-group-item">  Span: {this.hitProper.span} </li>
                    </ul>
                   {/* <button 
                      onClick={() => {
                        this.setState(state => ({ limit: state.limit + 2 }));
                      }}
                    > 
                      Load more
                    </button> */} 
                  </React.Fragment>
                  <label className="margin">This is {pushHit} </label><br/><br/>
                  <label className="red">Nomer of hit: {this.hitProper.counter-4}. </label><br/><br/>
                  <label for="Part1">Hit Part1:</label>
                  <progress id="Part1" max="100" value={this.power.Part1/50}> {this.power.Part1} % </progress>
                  <output> {this.power.Part1} </output><br/>
                  <label for="Part2">Hit Part2:</label>
                  <progress id="Part2" max="100" value={this.power.Part2/50}> {this.power.Part2} % </progress>
                  <output> {this.power.Part2} </output><br/>
                  <label for="Part3">Hit Part3:</label>
                  <progress id="Part3" max="100" value={this.power.Part3/50}> {this.power.Part3} %</progress>
                  <output> {this.power.Part3} </output><br/>
                  <label for="Part4">Hit Part4:</label>
                  <progress id="Part4" max="100" value={this.power.Part4/50}> {this.power.Part4} %</progress>
                  <output> {this.power.Part4} </output>
                  </div>
                );
              }}
            </FirebaseDatabaseNode>
          </FirebaseDatabaseProvider>
        </div>
      );
    }
  }

render(<App />, document.getElementById("root"));
