import React from 'react'

import SwapiService from '../../Services/swapi-service'

import './person-detales.css'

export default class PersonDetales extends React.Component {

    state={
        personData:{}
    }
    componentDidMount(){
        this.updatePersonData(this.props.person); 
    }

    componentDidUpdate(props){
        if(this.props.person!==props.person){
            this.updatePersonData(this.props.person);
        }
    }

    updatePersonData = (id)=> {
     const swapi = new SwapiService();
     swapi.getPerson(id).then((person)=>this.setState({personData:person}))
    }

    render() {
        const { name, data } = this.personData;
        let ShowInfo;

    
            const ListData = () => data.map(
                (el) => {
                    return (<li className="list-group-item">{el}</li>);
                });
            const ShowInfoData = () => {
                return (
                    <div className="random-planet list-group-item">
                        <img className="Person-image"
                            src={`https://starwars-visualguide.com/assets/img/characters/${this.props.person}.jpg`} alt='pict show' />
                        <div className="flex-column">
                            <h4 className="test">{name}</h4>
                            <ul className="list-group-flash">
                                <ListData />
                            </ul>
                        </div>
                    </div>);
            };

            ShowInfo = ShowInfoData;
    
        return (
            <div>
                <ShowInfo />
            </div>
        );

    }
}
