export default class SwapiService {

    _apiBase = 'https://swapi.co/api';

    async getResourse (url) {
        const res = await fetch(`${this._apiBase}${url}`); 

        if(!res.ok){
            throw new Error ( 'missing '+res.status);
        }
        const body = await res.json();
        return body;
    };

    getAllPeople= async()=>{
        const res = await this.getResourse(`/people/`);
        return res.results;
    }
    
    async getPerson(id){
        const el = await this.getResourse(`/people/${id}/`);
        const person = {
            id,
            name:el.name,
        gender:el.gender,
        birth_year:el.birth_year,
        eye_color:el.eye_color};
        return person;
    } 
    
    async getAllStarships(){
        const res = await this.getResourse(`/starships/`);
        return res.results;
    }
    
    getStarShip(id){
        return this.getResourse(`/starships/${id}/`);
    }   

    async getAllPlanets(){
        const res = await this.getResourse(`/planets/`);
        return res.results;
    }
    
    async getPlanet(id){
        const el = await this.getResourse(`/planets/${id}/`)
        const planet={id:id,
            name:el.name,
            population:el.population,
            rotation_period:el.rotation_period,
            diameter:el.diameter,
            climate:el.climate,
            surface_water:el.surface_water}
        return planet;
    }
}
