import React from 'react'

import SwapiService from '../../Services/swapi-service'

import './person-detales.css'

export default class PersonDetales extends React.Component {

    state={
        personData:{}
    }
    componentDidMount(){
        this.updatePersonData(this.props.person); 
    }

    componentDidUpdate(props){
        if(this.props.person!==props.person){
            this.updatePersonData(this.props.person);
        }
    }

    updatePersonData = (id)=> {
     //const swapi = new SwapiService();
     const getData = this.props.metod;
     getData(id).then((person)=>this.setState(()=>{
         return {personData:person};}));
    }

    render() {
      
      const { name, id , eye_color} = this.state.personData;

             
        return (
            <div className="jumbotron zeal-test-2">{
             } 
              <img className="Person-image"
                            src={`https://starwars-visualguide.com/assets/img/characters/${id}.jpg`} alt='pict show' />
             <span>{name} color eyes {eye_color}</span>
            </div>
        );

    }
}
