import React from 'react';
import SwapiService from '../../Services/swapi-service'

import Header from '../header'
import ItomList from '../itom-list'
import PersonDetales from '../person-detales'
import RandomPlanet from '../random-planet'
//import StarShipDetales from '../starship-detales'

import './app.css'

export default class App extends React.Component {

swip = new SwapiService();     
state = {
     personId : 1,
     person: {},
     planet:{},
     starShip:{},
     peopleList:[{name:"test"},{birth_year:"123"}]
}

// constructor(){
//      super();
//      this.getPersonParam();
//      this.getPeopleList();
// }     

setIdItem= (el)=>{
     this.setState({personId:el});
     console.log("person Id: "+this.state.personId);
     this.getPersonParam();
}
   
getPersonParam(){ 
     this.swip.getPerson(this.state.personId)
         .then((el)=>{
          this.setState({person:el});
          console.log("person param: "+this.state.person);    
 });};
getPeopleList(){
     let count=1; 
     this.swip.getAllPeople().then(async (el)=>{
         await this.setState({peopleList:el.map((el)=>{
               const newEl = {id:count,...el};
               count++;
         return newEl})}); 
 });}

render(){

  return(
  <div>
      <div className = "stardb-app ">
          <Header />
          <RandomPlanet/>
       </div>
       <div className = "row ">
            <ItomList
               usinItem={this.setIdItem}
               />           
       </div>
       <div className="right-bar">
            <PersonDetales  
               person={this.state.person}
               peopleList={this.state.peopleList}/>
     </div>
    </div>
   );
};
}

       
