import React, { Component } from 'react';
import {FirebaseDatabaseMutation}
                         from "@react-firebase/database";

import './set-sensetive.css';


export default class SetSensetive extends Component {

  state = {
    person: null,
    setSensetive : 1,
    setFront : 1
  };

  setSens = (event)=>{
    let evValue=event.target.value;
    console.log("event"+evValue);
    this.setState({setSensetive:evValue});
  }

  setFrontF=(event)=>{
    this.setState({setFront:event.target.value});
  }

  render() {
    console.log(this.props);
    const path = this.props.path;
    return (
      <div>

          <FirebaseDatabaseMutation type="set" path={path}>
             {({ runMutation }) => {
                return (
                  <div>
                  <input type = "number" size="12" className="butnSens" onChange ={this.setSens}/>
                    <button
                      data-testid="test-push"
                      onClick={async () => {
                        const { key } = await runMutation({ PARAM: this.state.setSensetive });
                        this.setState({ pushedKey: key });
                        console.log("key"+key);
                      }}
                    > {this.props.text}
                    </button><br/>

{/*                    <input type = "number" size="17" className="butnSens" onChange ={this.setFrontF}/>
                    <button
                      data-testid="test-push2"
                      onClick={async () => {
                        const { key } = await runMutation({ FRONT: this.state.setFront });
                        this.setState({ pushedKey: key });
                        console.log("key"+key);
                      }}
                    > set Front
                    </button>
                    */}
                  </div>
                );
              }}
          </FirebaseDatabaseMutation>
      </div>
    )
  }
}
