import React from 'react';
import './app.css';


import {
  FirebaseDatabaseProvider,
  FirebaseDatabaseNode
// , FirebaseDatabaseMutation
       } from "@react-firebase/database";
import * as firebase from "firebase/app";
import "@firebase/database";
import { config } from "../../config";
import SetSensetive from "../setSensetive"
import ProgerssBars from "../progerss-bar"
import PropertyList from "../property-list"
import Header from "../header"

import {BrowserRouter as Router, Switch, Route, IndexRoute} from 'react-router-dom';


const styles = {
  fontFamily: "sans-serif"
};

export default class App extends React.Component {


  render() {
    return (
    <div style={styles}>
      <Router>
         <div className="grey">
            <Header/>
         </div>
         <Switch>
            <Route path="/fights" component={FireBaseFragmnt} />
         </Switch>
      </Router>        
     </div>
    );
  }
  }

  class FireBaseFragmnt extends React.Component {

    state = {
      pushedKey: ''
    };

  hitProper = {
      part:  0,
      power: 0,
      long:  0,
      span:  0,
      time: 0,
      counter: 0
    };


destructuringJson (string){
      this.hitProper.part=string.part;
      this.hitProper.long=string.long;
      this.hitProper.power=string.power;
      this.hitProper.span=string.incr;
      this.hitProper.time=string.time;
    }


    render(){
      return(
        <div className="send-data">
        <FirebaseDatabaseProvider firebase={firebase} {...config}>
          <FirebaseDatabaseNode path="HS1/" >
            {d => {
              const object = d.value;
              let hitData = [];
              if (object !== null) {
                for (let key in object) {
                  if (key === "hits") {
                    for (let keyH in object.hits) {
                      hitData.push(object.hits[keyH]);
                    }
                  }
                }
                this.destructuringJson(hitData[hitData.length - 1]);
              }
              const stylePoint={
                point1:{  width: this.hitProper.power/15+"px",
                          height: this.hitProper.power/15+"px",
                          lineHeight: this.hitProper.power/15+"px",
                          left: "20px",
                          top:"-350px",
                          zIndex: 2
                        },
                point2:{ width: 500/25+"px",
                         height: 500/25+"px",
                         lineHeight: 500/25+"px",
                         left: "160px",
                         top:"-450px",
                         zIndex: 3
                      },
                point3:{},
                point4:{},
              }
              return (
                <div className='d-flex '> 
                  <ul className='list-group  list-group-horizontal '>
                    <li className="list-group-item li-css">
                       <React.Fragment>
                         <h1>HIT PARAMS</h1> 
                         <PropertyList property={this.hitProper} /><br/><br/><br/>
                         <SetSensetive path="HS1/param" btnText="set sensitivity" />
                       </React.Fragment> </li>
                    <li className="list-group-item li-css"> 
                       <img src={require('../../body1.png')} alt='d2'/> 
                       <div className="circle" style={stylePoint.point1}>{this.hitProper.power}</div>
                       <div className="circle" style={stylePoint.point2}>{this.hitProper.power}</div>
                    </li>  
                    <li className="list-group-item li-css" >
                        <ProgerssBars hitProper={this.hitProper} counter={hitData.length} allHit={hitData} />
                     </li>
                   </ul>
                </div>
              );
            }}
          </FirebaseDatabaseNode>
        </FirebaseDatabaseProvider>

        </div>

      );
    }

  }
