import React from 'react'

import './person-detales.css'

export default class PersonDetales extends React.Component {


    render() {
        const { id, foto, name, data } = this.props.person;
        let ShowInfo;

        if (id > 0) {
            const ListData = () => data.map(
                (el) => {
                    return (<li className="list-group-item">{el}</li>);
                });
            const ShowInfoData = () => {
                return (
                    <div className="random-planet list-group-item">
                        <img className="Person-image"
                            src={foto} alt='pict show' />
                        <div className="flex-column">
                            <h4 className="test">{name}</h4>
                            <ul className="list-group-flash">
                                <ListData />
                            </ul>
                        </div>
                    </div>);
            };

            ShowInfo = ShowInfoData;
        } else {
            ShowInfo = () => <h3 className="random-planet list-group-item"> {name}</h3>
        }

        return (
            <div>
                <ShowInfo />
            </div>
        );

    }
}
