import React, { Component } from 'react';
import './show-hit.css';


export default class ShowHit extends Component {

  state = {
    stylePoint:{
    width: Math.cbrt(power)*3.5+"px",
    height: Math.cbrt(power)*3.5+"px",
    lineHeight: Math.cbrt(power)*3.5+"px",
    zIndex: 2,
    left: "0px",
    top:"0px"
}
  };

  render() {
    const {part,power} = this.props;

    this.setState({stylePoint:{width:Math.cbrt(power)*3.5+"px"}})

    switch(part){
      case 1: this.setState({stylePoint:{left : "80px"}});
              this.setState({stylePoint:{top :  "210px"}});
        break;
      case 2: this.setState({stylePoint:{left : "195px"}});
              this.setState({stylePoint:{top :  "210px"}});
              break;
      case 3: this.setState({stylePoint:{left : "160px"}});
              this.setState({stylePoint:{top :  "150px"}});
              break;
      case 4: this.setState({stylePoint:{left :  "115px"}});
              this.setState({stylePoint:{top :  "150px"}});
              break;
      default: break;
    }

    // function resetHit () {
    //    stylePoint = {width:0,height:0,lineHeight:0,zIndex:2,left:"0px",top:"0px"};
    //    console.log("reset HIT done");
    // }

    // setTimeout(resetHit,2000);

    return (
      <div> 
                     <img src={require('../../body1.png')} alt='d2'/> 
                     <div className="circle" style={this.state.stylePoint}>{power}</div>   

      </div>
    )
  }
}
