import React, { Component } from 'react';
import './show-hit.css';


export default class ShowHit extends Component {

  state = {
  };




  render() {
    const {part,power} = this.props;

    let stylePoint={
     // point1:{ 
                width: Math.cbrt(power)*3.5+"px",
                height: Math.cbrt(500)*3.5+"px",
                lineHeight: Math.cbrt(power)*3.5+"px",
                zIndex: 2
       //       }
       }
    switch(part){
      case 1: stylePoint={
        width: Math.cbrt(power)*3.5+"px",
        height: Math.cbrt(power)*3.5+"px",
        lineHeight: Math.cbrt(power)*3.5+"px",
        zIndex: 2,
        left: "80px",
        top:"210px"};
        break;
      case 2: stylePoint={left: "195px",
              top:"210px"};
              break;
      case 3: stylePoint={left: "160px",
              top:"150px"};
              break;
      case 4: stylePoint={left: "115px",
              top:"150px"};
              break;
      default: break;
    }


    return (
      <div> 
                     <img src={require('../../body1.png')} alt='d2'/> 
                     <div className="circle" style={stylePoint}>{power}</div>   

      </div>
    )
  }
}


