import React, { Component } from 'react';
import {BrowserRouter as Router, Route, IndexRoute} from 'react-router-dom';
//BrowserRouter
import Header from '../header';
import RandomPlanet from '../random-planet';
import ErrorButton from '../error-button';
import ErrorIndicator from '../error-indicator';
import PeoplePage from '../people-page';
import PlanetPage from '../planet-page';


import './app.css';

export default class App extends Component {

  state = {
    showRandomPlanet: true,
    hasError: false
  };

  toggleRandomPlanet = () => { 
     console.log(this.path);
    console.log('hi');

    this.setState((state) => {
      return {
        showRandomPlanet: !state.showRandomPlanet
      }
    });
  };

  componentDidCatch() {
    this.setState({ hasError: true });
  }

  render() {

    if (this.state.hasError) {
      return <ErrorIndicator />
    }

    const planet = this.state.showRandomPlanet ?
      <RandomPlanet/> :
      null;

    return (
    <Router>
    <div classNam="stardb-app">
        <Header />
        { planet }

        <div className="row mb2 button-row">
          <button
            className="toggle-planet btn btn-warning btn-lg"
            onClick={this.toggleRandomPlanet}>
            Toggle Random Planet  
                    </button>
             <button > push {JSON.stringify(this.props.location)}</button>      

          <ErrorButton />
        </div>

        <Route exact path = '/' component ={PeoplePage} />
        <Route path = '/PeoplePAge' component= { PlanetPage}/>
        <Route path = '/PlanetPage' component= { PeoplePage}/>
        <Route path = '/starships' component= { PeoplePage}/>


      </div>
      </Router>
    );
  }
}
