import React from 'react'
import SwapiService from '../../Services/swapi-service'

import './random-planet.css'

export default class RandomPlanet extends React.Component {

   state = {
       planetS : {id:'5',
                  name:"",
                  population:'',
                  rotation_period: "",
                  diameter:''}
   }

   render(){ 
   const swip = new SwapiService();
 
    setInterval(()=>{
        this.setState(()=>{
            return {id:Math.floor(Math.random()*27+2)};
        }); 
    },2000);

   swip.getPlanet(this.state.planetS.id)
       .then(async (el)=>{
           await this.setState({name:el.name,
                      population:el.population,
                      rotation_period:el.rotation_period,
                      diameter:el.diameter});    
    });

   //const {name, rotation_period,orbital_period,diameter }=planetC;
  // console.log(name +' '+ rotation_period+" "+orbital_period+' '+diameter)   

   return(
       <div   className="random-planet list-group-item">
         <img className="planet-image"
             src = {`https://starwars-visualguide.com/assets/img/planets/${this.state.planetS.id}.jpg`} alt = 'pict show'/>
         <div className="flex-column"> 
               <h4 className = "test">{this.state.name}</h4>
          <ul className = "list-group-flash">
             <li className=" list-group-item">
                    <span className= "term">Population  </span>
                    <span>{this.state.population}</span>
                </li>
                <li className="list-group-item">
                    <span className= "term">Rotation Period</span>
                    <span>{this.state.rotation_period}</span>
                </li>
                <li className=" list-group-item">
                    <span className= "term">Diametr</span>
                    <span>{this.state.diameter}</span>
                </li>
             </ul>
        </div>
            
       </div>
   );
};
}
