import React from 'react';
import SwapiService from '../../Services/swapi-service'

import Header from '../header'
import ItomList from '../itom-list'
import PersonDetales from '../person-detales'
import RandomPlanet from '../random-planet'
//import StarShipDetales from '../starship-detales'

import './app.css'

export default class App extends React.Component {

swip = new SwapiService();     
state = {
     person:{},
     planet:{},
     starShip:{},
     personS: [],
     planetS:[],
     starShipS:[],
     list:[]//{id:NaN,type:NaN,param:[]}
}

constructor(){
     super();
     this.getPeopleList();
}     

setIdItem= (el,type)=>{
      this.setState({person:this.state.personS[el-1]});
      console.log('type'+type);
}

setList=(list)=>{
   switch(list){
     case "people":
          this.getPeopleList();
          break;
     case "planet":
          this.getPlanetleList();
          break;
     case "starShip":
          this.getStarShipList();
          break;
     default: this.setState({});
   }
}

getPeopleList(){
     let count=1; 
     
     this.swip.getAllPeople().then(async (el)=>{
         await this.setState({personS:el.map((el)=>{
               const newEl = {id:count,
                              foto: `https://starwars-visualguide.com/assets/img/characters/${count}.jpg`,
                              name:el.name, 
                              data: 
                              [el.gender, 
                              el.birth_year,
                              el.eye_color]};
               count++;
         return newEl})});
     this.setState({list:this.state.personS.map((el)=>{ 
          return {id:el.id, type:"people",pole:el.name+" ("+el.birth_year+")"}})})
 });}

 getPlanetleList(){
     let count=1; 
     
     this.swip.getAllPlanets().then(async (el)=>{
         await this.setState({planetS:el.map((el)=>{
               const newEl = {id:count,
                              foto: `https://starwars-visualguide.com/assets/img/planets/${count}.jpg`,
                              name:el.name,
                              data:
                              [el.population,
                              el.rotation_period,
                              el.diameter,
                              el.climate,
                              el.surface_water]};
               count++;
         return newEl})});
     this.setState({list:this.state.planetS.map((elem)=>{ 
          return {id:elem.id, type:"planet",pole:elem.name}})})
 });}

 getStarShipList(){
     let count=1; 
     
     this.swip.getAllStarships().then(async (el)=>{
         await this.setState({starShipS:el.map((el)=>{
               const newEl = {id:count,
                              foto:`https://starwars-visualguide.com/assets/img/starships/${count}.jpg`,
                              name:el.name,
                              data:
                              [el.model,
                              el.manufacturer,
                              el.cost_in_credits,
                              el.length,
                              el.crew,
                              el.passengers,
                              el.cargo_capacity]};
               count++;
               console.log(newEl);
         return newEl})});
     this.setState({list:this.state.starShipS.map((el)=>{ 
          return {id:el.id, type:"starShip",pole:el.name}})})
 });}

render(){

  return(
  <div>
      <div className = "stardb-app ">
          <Header 
          chouseList={this.setList}/>
          <RandomPlanet/>
       </div>
       <div className = "row ">
            <ItomList
               usinItem={this.setIdItem}
               peopleList={this.state.list}
               />           
       </div>
       <div className="right-bar">
            <PersonDetales  
               person={this.state.person}/>
     </div>
    </div>
   );
};
}
