import React from 'react';
import './app.css';


import {
  FirebaseDatabaseProvider,
  FirebaseDatabaseNode
// , FirebaseDatabaseMutation
       } from "@react-firebase/database";
import * as firebase from "firebase/app";
import "@firebase/database";
import { config } from "../../config";
import SetSensetive from "../setSensetive"
import ProgerssBars from "../progerss-bar"
import PropertyList from "../property-list"


const styles = {
  fontFamily: "sans-serif"
};

export default class App extends React.Component {

  state = {
      pushedKey: ''
    };

  hitProper = {
      part:  0,
      power: 0,
      long:  0,
      span:  0,
      time: 0,
      counter: 0
    };



// destructuring (string) {
//       if(string!==undefined){
//        // console.log('nomer'+string.indexOf(':'));

//       if(string.indexOf(':')===0){
//         this.hitProper.part=string.slice(1,2);
//         this.hitProper.long=string.slice(3,string.indexOf(":",3));
//         this.hitProper.power=string.slice(string.indexOf(":",3)+1,string.indexOf(":",10));
//         this.hitProper.span=string.slice(string.indexOf(":",9)+1);
//       }}
//    //   console.log(typeof(this.hitProper.part));
//        this.setPowerProgress(this.hitProper.part);
//     }



destructuringJson (string){
      this.hitProper.part=string.part;
      this.hitProper.long=string.long;
      this.hitProper.power=string.power;
      this.hitProper.span=string.incr;
      this.hitProper.time=string.time;
    }

    render() {
      return (
        <div style={styles}>
          <FirebaseDatabaseProvider firebase={firebase} {...config}>
            <FirebaseDatabaseNode  path="HS1/" // orderByKey  
                                           >
              {d => {
                this.hitProper.counter++;
                const object = d.value;
                let hitData = [];
                let returnParam = [];
                let returnFront = [];
               // let value=33;
               if(object!==null){ 
                  for(let key in object){
                    if(key==="hits"){
                      for(let keyH in object.hits){ 
                        hitData.push(object.hits[keyH]);//.original
                  }  }
                  console.log('hitdata');
                  console.log(hitData);

                    for(let keyN in object[key]){
                        if(keyN==="original"){
                         // hitData.push(object[key]);//.original
                        }
                        else if(keyN==="PARAM"){
                          returnParam.push(object[key].PARAM);
                        }
                        else if(keyN==="FRONT"){
                          returnFront.push(object[key].FRONT);
                        }
                   } 
                }
                console.log("returnParam");
                console.log(returnParam);

                console.log("returnFront");
                console.log(returnFront);

                console.log(hitData[hitData.length-1]);
                this.destructuringJson(hitData[hitData.length-1]);
      }
      return (
                  <div className="bigText" ><h1>HIT SYSTEM</h1>

      <React.Fragment>
                <img src="http://www.finsmes.com/wp-content/uploads/2018/11/visiblebody.png" alt=""></img>

      <PropertyList property={this.hitProper}/>
      <SetSensetive path = "HS1/param" btnText="set sensitivity"/>

     </React.Fragment><br/>

     <ProgerssBars hitProper={this.hitProper} counter={hitData.length}/>
                </div>
                );
              }}
            </FirebaseDatabaseNode>
          </FirebaseDatabaseProvider>
        </div>
      );
    }
  }
