import React from 'react'
import SwapiService from '../../Services/swapi-service'

import './person-detales.css'

export default class PersonDetales extends React.Component {

   state = {  person : {} }

   constructor(){
       super();
       this.getPersonParam();
   }

   swip = new SwapiService();
   
   
   getPersonParam(){ 
       this.swip.getPerson(this.props.id)
           .then((el)=>{
            this.setState({person:el} );
             console.log(el);    
      });};

   render() { 
  
      

   //const {name, rotation_period,orbital_period,diameter }=planetC;
  // console.log(name +' '+ rotation_period+" "+orbital_period+' '+diameter)   

   return(
       <div   className="random-planet list-group-item">
         <img className="Person-image"
             src = {`https://starwars-visualguide.com/assets/img/characters/${this.state.id}.jpg`} alt = 'pict show'/>
         <div className="flex-column"> 
               <h4 className = "test">{this.state.name}</h4>
          <ul className = "list-group-flash">
             <li className=" list-group-item">
                    <span className= "term">Gender:   </span>
                    <span>{this.state.gender}</span>
                </li>
                <li className="list-group-item">
                    <span className= "term">Birth year: </span>
                    <span>{this.state.birth_year}</span>
                </li>
                <li className=" list-group-item">
                    <span className= "term">Eye_color: </span>
                    <span>{this.state.eye_color}</span>
                </li>
             </ul>
        </div>
            
       </div>
   );
};
}
