import React from 'react';
import './index.css';

import { render } from "react-dom";
import {
  FirebaseDatabaseProvider,
  FirebaseDatabaseNode
// , FirebaseDatabaseMutation
       } from "@react-firebase/database";
import * as firebase from "firebase/app";
import "@firebase/database";
import { config } from "./config";
import SetSensetive from "./components/setSensetive/set-sensetive.js"

const styles = {
  fontFamily: "sans-serif"
};

//const s = (a: any) => JSON.stringify(a, null, 2);

export default class App extends React.Component {

  state = {
    //  limit: 20,
      pushedKey: ''
    };

  hitProper = {
      part:  0,
      power: 0,
      long:  0,
      span:  0,
      time: 0,
      counter: 0
    };

  power = {
      Part1: 0,
      Part2: 0,
      Part3: 0,
      Part4: 0,
   };

// destructuring (string) {
//       if(string!==undefined){
//        // console.log('nomer'+string.indexOf(':'));

//       if(string.indexOf(':')===0){
//         this.hitProper.part=string.slice(1,2);
//         this.hitProper.long=string.slice(3,string.indexOf(":",3));
//         this.hitProper.power=string.slice(string.indexOf(":",3)+1,string.indexOf(":",10));
//         this.hitProper.span=string.slice(string.indexOf(":",9)+1);
//       }}
//    //   console.log(typeof(this.hitProper.part));
//        this.setPowerProgress(this.hitProper.part);
//     }

setPowerProgress (num){
      switch(num){
        case 1:
            for(let key in this.power){this.power[key]=0;}
            this.power.Part1=this.hitProper.power;
            break;
        case 2:
        for(let key in this.power){this.power[key]=0;}
            this.power.Part2=this.hitProper.power;
            break;
        case 3:
        for(let key in this.power){this.power[key]=0;}
            this.power.Part3=this.hitProper.power;
            break;
        case 4:
        for(let key in this.power){this.power[key]=0;}
            this.power.Part4=this.hitProper.power;
            break;
        default: break;
        }

    }

destructuringJson (string){
      this.hitProper.part=string.part;
      this.hitProper.long=string.long;
      this.hitProper.power=string.power;
      this.hitProper.span=string.incr;
      this.hitProper.time=string.time;
      this.setPowerProgress(this.hitProper.part);
    }

    render() {
      return (
        <div style={styles}>
          <FirebaseDatabaseProvider firebase={firebase} {...config}>
            <FirebaseDatabaseNode  path="HS1/" //  limitToFirst={this.state.limit}
                                   orderByKey  // orderByValue={"created_on"}
                                           >
              {d => {
                this.hitProper.counter++;
                const object = d.value;
                let hitData = [];
                let returnParam = [];
                let returnFront = [];
                let pushHit = "";
               // let value=33;
               if(object!==null){ 
                  for(let key in object){
                    if(key==="hits"){
                      for(let keyH in object.hits){ 
                        hitData.push(object.hits[keyH]);//.original
                  }  }
                  console.log('hitdata');
                  console.log(hitData);

                    for(let keyN in object[key]){
                        if(keyN==="original"){
                         // hitData.push(object[key]);//.original
                        }
                        else if(keyN==="PARAM"){
                          returnParam.push(object[key].PARAM);
                        }
                        else if(keyN==="FRONT"){
                          returnFront.push(object[key].FRONT);
                        }
                   } 
                }
                console.log("returnParam");
                console.log(returnParam);

                console.log("returnFront");
                console.log(returnFront);
                //Object.values(object);
                console.log(hitData[hitData.length-1]);
                this.destructuringJson(hitData[hitData.length-1]);
                if(this.hitProper.span<150){
                     pushHit="HIT";
                } else{
                  pushHit="PUSH !!!";
                }}
                return (
                  <div className="bigText" ><h1>HIT SYSTEM</h1>

      <React.Fragment>
                <img src="http://www.finsmes.com/wp-content/uploads/2018/11/visiblebody.png" alt=""></img>
                {/*<h4>Value:  {hitData[hitData.length-1]}</h4>*/}
                <ul className="list-group list-group-horizontal">
                    <li className="list-group-item marg">
                        <ul className="list-group marg ">
                            <li className="list-group-item marg " >  Part:      </li>
                            <li className="list-group-item marg">    Distance:  </li> 
                            <li className="list-group-item marg">    Power:     </li>
                            <li className="list-group-item marg">    Span:      </li>
                            <li className="list-group-item marg">    Time:      </li>
                         </ul></li>
                    <li className="list-group-item marg" >
                        <ul className="list-group marg">
                            <li className="list-group-item marg" >  {this.hitProper.part}  </li>
                            <li className="list-group-item marg" >  {this.hitProper.long}  </li> 
                            <li className="list-group-item marg">   {this.hitProper.power} </li>
                            <li className="list-group-item marg">   {this.hitProper.span}  </li>
                            <li className="list-group-item marg">   {this.hitProper.time}  </li>
                        </ul>
                    </li></ul>
                   {/* <button onClick={() => {this.setState(state => ({ limit: state.limit + 2 }));
                      }} > Load more  </button> */} 
      <SetSensetive path = "HS1/param" btnText="set sensitivity"/>
      {//<SetSensetive path = "HS1/front" btnText="set Front"/>
      }
     </React.Fragment><br/>

                  <h2 className="margin bigText">This is {pushHit} </h2><br/>
                  <h3 className="red">Hit count: {hitData.length} </h3><br/><br/> {/*this.hitProper.counter-4*/}
                  <label for="Part1">Hit Part1:</label>
                  <progress id="Part1" max="100" value={this.power.Part1/50}> {this.power.Part1} % </progress>
                  <output> {this.power.Part1} </output><br/>
                  <label for="Part2">Hit Part2:</label>
                  <progress id="Part2" max="100" value={this.power.Part2/50}> {this.power.Part2} % </progress>
                  <output> {this.power.Part2} </output><br/>
                  <label for="Part3">Hit Part3:</label>
                  <progress id="Part3" max="100" value={this.power.Part3/50}> {this.power.Part3} %</progress>
                  <output> {this.power.Part3} </output><br/>
                  <label for="Part4">Hit Part4:</label>
                  <progress id="Part4" max="100" value={this.power.Part4/50}> {this.power.Part4} %</progress>
                  <output> {this.power.Part4} </output>
                </div>
                );
              }}
            </FirebaseDatabaseNode>
          </FirebaseDatabaseProvider>
        </div>
      );
    }
  }

render(<App />, document.getElementById("root"));
