import React from 'react'
import SwapiService from '../../Services/swapi-service'

import './person-detales.css'

export default class PersonDetales extends React.Component {

   state = {  person : {},
              personId:"1" }

   constructor(){
       super();
       this.getPersonParam(this.state.personId);
       this.testProps();
   }
   testProps=(props)=>{console.log("chek props  "+props);
                       return 0;   };
   swip = new SwapiService();
      
   getPersonParam(id){ 
       this.swip.getPerson(id)
           .then((el)=>{
            this.setState({person:el});
             console.log(this.state.person);    
      });};

    // chengId () {
    //      this.setState({personId:this.props.id});
    //       console.log("change ID:"+this.state.personId);
    //     }

   render() { 
      // this.setState({person:this.props.id})
    const {id, name, gender,birth_year, eye_color} = this.state.person; 
      
   return(
       <div   className="random-planet list-group-item">
         <img className="Person-image"
             src = {`https://starwars-visualguide.com/assets/img/characters/${id}.jpg`} alt = 'pict show'/>
         <div className="flex-column"> 
               <h4 className = "test">{name}</h4>
          <ul className = "list-group-flash">
             <li className=" list-group-item">
                    <span className= "term">Gender:   </span>
                    <span>{gender}</span>
                </li>
                <li className="list-group-item">
                    <span className= "term">Birth year: </span>
                    <span>{birth_year}</span>
                </li>
                <li className=" list-group-item">
                    <span className= "term">Eye_color: </span>
                    <span>{eye_color}</span>
                </li>
             </ul>
        </div>
            
       </div>
   );
};
}
