import React from 'react';
import SwapiService from '../../Services/swapi-service'

import Header from '../header'
import ItomList from '../itom-list'
import PersonDetales from '../person-detales'
import RandomPlanet from '../random-planet'
//import StarShipDetales from '../starship-detales'

import './app.css'

export default class App extends React.Component {

swip = new SwapiService();     
state = {
     person:{},
     personId : 1,
     personS: [],
     planet:{},
     starShip:{},
     setList:[],
     list:{id:NaN,type:NaN,param:[]}
}

constructor(){
     super();
     this.getPeopleList();
}     

setIdItem= (el)=>{
      this.setState({personId:el});
      console.log("person Id: "+this.state.personId);
      this.setState({person:this.state.personS[this.state.personId-1]});
}

setList=(list)=>{
   switch(list){
     case "people":
          this.getPeopleList();
          break;
     case "planet":
          this.getPlanetleList();
          break;
     case "starShip":
          this.getStarShipList();
          break;
     default: this.setState({});
   }
}

getPeopleList(){
     let count=1; 
     
     this.swip.getAllPeople().then(async (el)=>{
         await this.setState({personS:el.map((el)=>{
               const newEl = {id:count, 
                              name:el.name, 
                              gender: el.gender, 
                              birth_year:el.birth_year,
                              eye_color:el.eye_color,
                              foto: `https://starwars-visualguide.com/assets/img/characters/${count}.jpg`};
               count++;
         return newEl})});
     this.setState({list:this.state.personS.map((el)=>{ 
          return {id:el.id, pole:el.name+" ("+el.birth_year+")"}})})
 });}

 getPlanetleList(){
     let count=1; 
     
     this.swip.getAllPlanets().then(async (el)=>{
         await this.setState({setList:el.map((el)=>{
               const newEl = {id:count,...el};
               count++;
         return newEl})});
     this.setState({list:this.state.setList.map((el)=>{ 
          return {id:el.id, pole:el.name}})})
 });}

render(){

  return(
  <div>
      <div className = "stardb-app ">
          <Header 
          chouseList={this.setList}/>
          <RandomPlanet/>
       </div>
       <div className = "row ">
            <ItomList
               usinItem={this.setIdItem}
               peopleList={this.state.list}
               />           
       </div>
       <div className="right-bar">
            <PersonDetales  
               person={this.state.person}/>
     </div>
    </div>
   );
};
}
